import { useQuery, gql } from '@apollo/client';
import client from "./apollo.client";
import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import 'bootstrap/dist/css/bootstrap.min.css';
import {CardDeck} from "react-bootstrap";
const FEED_QUERY = gql`
        query {
            getCountries {
                name
                emoji
            }
        }
`;

const CountryList = () => {
    const [data, setData] = useState([{}]);
    const parameters = [
        "languages",
        "currency",
        "capital",
        "native",
        "phone",
        "continent"
    ]
    client
        .query({
            query: FEED_QUERY
        })
        .then((result) => {
            setData(result.data.getCountries);
            console.log(result);
        })
        .catch((error) => {
            console.log(error);
        });

    let cardStyle = {
        boxShadow: "1px 3px 1px #9E9E9E",
        textAlign: "center"
    }

    const changeParameters = (param) => {
        if (parameters.includes(param)) {
            let i = parameters.indexOf(param)
            parameters.splice(i, 1);
        } else {
            parameters.push(param);
        }
    }


    return (

        <div style={{marginLeft: 15}}>
            <CardDeck style={{margin: 20}}>
                <Card style={cardStyle} onClick={() => {
                    changeParameters("languages");

                }
                }>
                    <Card.Body>
                        Languages
                    </Card.Body>
                </Card>
                <Card style={cardStyle}>
                    <Card.Body>
                        Currency
                    </Card.Body>
                </Card>
                <Card style={cardStyle}>
                    <Card.Body>
                        Capital
                    </Card.Body>
                </Card>
                <Card style={cardStyle}>
                    <Card.Body>
                        Native
                    </Card.Body>
                </Card>
                <Card style={cardStyle}>
                    <Card.Body>
                        Phone
                    </Card.Body>
                </Card>
                <Card style={cardStyle}>
                    <Card.Body>
                        Continent
                    </Card.Body>
                </Card>
            </CardDeck>
            {data.map(function(d, idx){
                return (
                    <Card style={{width: 300, margin: 15, boxShadow: "1px 3px 1px #9E9E9E"}}>
                        <Card.Header style={{fontSize: 40, padding: 5}}>
                            {d.emoji}
                        </Card.Header>
                        <Card.Body>
                            <Card.Title style={{fontSize: 25}}>
                                {d.name}
                            </Card.Title>
                        </Card.Body>
                    </Card>
                )
            })}
        </div>
    );
};


export default CountryList;
