import { useQuery, gql } from '@apollo/client';
import client from "./apollo.client";
import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import 'bootstrap/dist/css/bootstrap.min.css';
import {CardDeck} from "react-bootstrap";
const FEED_QUERY = gql`
        query {
            getCountries {
                name
                emoji
            }
        }
`;

const CountryList = () => {
    const [data, setData] = useState([{}]);
    const [toggles, setToggles] = useState(["1"]);
    const parameters = [
        "languages",
        "currency",
        "capital",
        "native",
        "phone",
        "continent"
    ]

    let blueHex = "#1982FC";
    client
        .query({
            query: FEED_QUERY
        })
        .then((result) => {
            setData(result.data.getCountries);
            console.log(result);
        })
        .catch((error) => {
            console.log(error);
        });

    let toggledCard = {
        boxShadow: "1px 3px 1px #9E9E9E",
        textAlign: "center",
        backgroundColor: "#1982FC"
    }

    let nonToggledCard = {
        boxShadow: "1px 3px 1px #9E9E9E",
        textAlign: "center",
        backgroundColor: "#FFFFFF"
    }

    const changeParameters = (param, index) => {
        if (parameters.includes(param)) {
            let i = parameters.indexOf(param)
            parameters.splice(i, 1);
        } else {
            parameters.push(param);
        }
        setToggles(toggles[index] === "0" ? "1" : "0")
    }


    return (

        <div style={{marginLeft: 15}}>
            <CardDeck style={{margin: 20}}>
                <Card
                    style={toggles[0] === "1" ? toggledCard : nonToggledCard}
                    onClick={() => {
                        changeParameters("languages", 0);
                    }
                    }>
                    <Card.Body>
                        Languages
                    </Card.Body>
                </Card>
                <Card style={toggles[1] === "1" ? toggledCard : nonToggledCard}>
                    <Card.Body>
                        Currency
                    </Card.Body>
                </Card>
                <Card style={toggles[2] === "1" ? toggledCard : nonToggledCard}>
                    <Card.Body>
                        Capital
                    </Card.Body>
                </Card>
                <Card style={toggles[3] === "1" ? toggledCard : nonToggledCard}>
                    <Card.Body>
                        Native
                    </Card.Body>
                </Card>
                <Card style={toggles[4] === "1" ? toggledCard : nonToggledCard}>
                    <Card.Body>
                        Phone
                    </Card.Body>
                </Card>
                <Card style={toggles[5] === "1" ? toggledCard : nonToggledCard}>
                    <Card.Body>
                        Continent
                    </Card.Body>
                </Card>
            </CardDeck>
            {data.map(function(d, idx){
                return (
                    <Card style={{width: 300, margin: 15, boxShadow: "1px 3px 1px #9E9E9E"}}>
                        <Card.Header style={{fontSize: 40, padding: 5}}>
                            {d.emoji}
                        </Card.Header>
                        <Card.Body>
                            <Card.Title style={{fontSize: 25}}>
                                {d.name}
                            </Card.Title>
                        </Card.Body>
                    </Card>
                )
            })}
        </div>
    );
};


export default CountryList;
